import React from "react";
import ReactDOM from "react-dom";

import "./styles.css";

class ListItems extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div>
        <li> First Items </li>
      </div>
    );
  }
}

class ToDoList extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div>
        <ListItems />
      </div>
    );
  }
}

class AddItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: this.props.defaultText };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.props.onSubmitForm(this.state.value);
    //this.props.onChangeText(this.state.value);
  }

  render() {
    return (
      <form className="inputForm" onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            value={this.state.value}
            onChange={this.handleChange}
          />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { listItems: [] };
    this.onSubmitForm = this.onSubmitForm.bind(this);
  }

  onSubmitForm(message) {
    let newMessage = [];
    newMessage.push(message);
    console.log(newMessage);
    this.setState({ listItems: newMessage });
  }

  render() {
    return (
      <div className="App">
        <h3>Hello CodeSandbox</h3>
        <ToDoList listItems={this.state.listItems} />
        <AddItem defaultText="Default Text" onSubmitForm={this.onSubmitForm} />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
