import React from "react";
import ReactDOM from "react-dom";

import "./styles.css";

class NewItem extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="SingleItem">
        <input
          type="checkbox"
          checked={this.props.item.isChecked}
          id="checkbox_"
        />
        <a
          href=""
          style={{
            textDecoration: this.props.item.isChecked ? "line-through" : "none"
          }}
        >
          {this.props.item.Category}
        </a>
      </div>
    );
  }
}

class ToDoList extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return this.props.listItems.map(obj => {
      <NewItem item={obj} />;
    });
  }
}

class AddItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };
  }

  handleChange = event => {
    this.setState({ value: event.target.value });
  };

  handleSubmit = event => {
    event.preventDefault();
    if (this.state.value.trim().length > 0) {
      this.props.onSubmitText(this.state.value);
    }
    this.setState({ value: "" });
  };

  render() {
    return (
      <form className="inputForm" onSubmit={this.handleSubmit}>
        <label>
          Add Item:
          <input
            type="text"
            value={this.state.value}
            onChange={this.handleChange}
          />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { data: [] };
  }

  onSubmitText = message => {
    let newObject = {};
    newObject["Category"] = message;
    newObject["isChecked"] = false;
    let temp = this.state.data;
    temp.push(newObject);
    this.setState({ data: temp });
  };

  render() {
    return (
      <div className="App">
        <div className="SplitScreen">
          <ToDoList listItems={this.state.data} />
          <AddItem onSubmitText={this.onSubmitText} />
        </div>
        <div className="SplitScreen">Second Div</div>
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
