import React from "react";
import ReactDOM from "react-dom";

import "./styles.css";

class DeleteButton extends React.Component {
  handleClick = () => {
    this.props.onDeleteItem(this.props.index);
  };

  render() {
    return <button onClick={this.handleClick}> Delete </button>;
  }
}

class EditForm extends React.Component {
  handleSubmit = event => {
    event.preventDefault();
    this.props.handleSubmit(this.props.index, this.props.currentText);
  };
  render() {
    return (
      <form className="inputForm" onSubmit={this.handleSubmit}>
        <label>
          Edit item:
          <input
            type="text"
            value={this.props.currentText}
            onChange={this.props.handleChange}
          />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

class EditButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = { showComponent: false };
  }

  handleClick = () => {
    this.setState({ showComponent: !this.state.showComponent });
  };

  render() {
    return (
      <div className="Btn">
        <button onClick={this.handleClick}> Edit </button>
        {this.state.showComponent ? (
          <EditForm
            currentText={this.props.currentText}
            handleChange={this.props.handleChange}
            handleSubmit={this.props.handleSubmit}
            index={this.props.index}
          />
        ) : null}
      </div>
    );
  }
}

class NewItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: this.props.item.Category };
  }

  handleEditChange = event => {
    this.setState({ value: event.target.value });
  };

  handleCheck = () => {
    this.props.onClickCheckCategory(this.props.index);
  };

  render() {
    return (
      <div className="SingleItem">
        <input
          type="checkbox"
          checked={this.props.item.isChecked}
          onChange={this.handleCheck}
          id="checkbox_"
        />
        <a
          href=""
          style={{
            textDecoration: this.props.item.isChecked ? "line-through" : "none"
          }}
        >
          {this.props.item.Category}
        </a>
        <EditButton
          currentText={this.state.value}
          handleChange={this.handleEditChange}
          handleSubmit={this.props.handleEditSubmit}
          index={this.props.index}
        />
        <DeleteButton
          index={this.props.index}
          onDeleteItem={this.props.onDeleteItem}
        />
      </div>
    );
  }
}

class ToDoList extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    if (this.props.listItems.length > 0) {
      return this.props.listItems.map((obj, index) => {
        return (
          <NewItem
            item={obj}
            onClickCheckCategory={this.props.onClickCheckCategory}
            index={index}
            handleEditSubmit={this.props.handleEditSubmit}
            onDeleteItem={this.props.onDeleteItem}
          />
        );
      });
    } else return <p> Empty </p>;
  }
}

class AddItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };
  }

  handleChange = event => {
    this.setState({ value: event.target.value });
  };

  handleSubmit = event => {
    event.preventDefault();
    if (this.state.value.trim().length > 0) {
      this.props.onSubmitText(this.state.value);
    }
    this.setState({ value: "" });
  };

  render() {
    return (
      <form className="inputForm" onSubmit={this.handleSubmit}>
        <label>
          Add Item:
          <input
            type="text"
            value={this.state.value}
            onChange={this.handleChange}
          />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { data: [] };
  }

  onClickCheckCategory = index => {
    let temp = this.state.data;
    temp[index].isChecked = !temp[index].isChecked;
    this.setState({ data: temp });
  };

  onSubmitText = message => {
    let newObject = {};
    newObject["Category"] = message;
    newObject["isChecked"] = false;
    let temp = this.state.data;
    temp.push(newObject);
    this.setState({ data: temp });
  };

  handleEditSubmit = (index, message) => {
    let newData = this.state.data;
    newData[index].Category = message;
    this.setState({ data: newData });
  };

  onDeleteItem = index => {
    let newData = this.state.data;
    newData.splice({ index }, 1);
    this.setState({ data: newData });
  };

  render() {
    return (
      <div className="App">
        <div className="SplitScreen">
          <ToDoList
            listItems={this.state.data}
            onClickCheckCategory={this.onClickCheckCategory}
            handleEditChange={this.props.handleEditChange}
            handleEditSubmit={this.handleEditSubmit}
            onDeleteItem={this.onDeleteItem}
          />
          <AddItem onSubmitText={this.onSubmitText} />
        </div>
        <div className="SplitScreen">Second Div</div>
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
