import React from "react";
import ReactDOM from "react-dom";

import "./styles.css";

class ToDoItems extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return this.props.data.map(item => {
      <li> {item.name} </li>;
    });
  }
}

class AddItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };
  }

  handleChange = event => {
    this.setState({ value: event.target.value });
  };

  handleSubmit = event => {
    event.preventDefault();
    if (this.state.value.trim().length > 0) {
      this.props.onSubmitText(this.state.value);
    }
    this.setState({ value: "" });
  };

  render() {
    return (
      <form className="inputForm" onSubmit={this.handleSubmit}>
        <label>
          Add Item:
          <input
            type="text"
            value={this.state.value}
            onChange={this.handleChange}
          />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { Categories: [], Items: [] };
  }

  onSubmitText = message => {
    let newObject = {};
    newObject["name"] = message;
    newObject["isChecked"] = false;
    let temp = this.state.Categories;
    temp.push(newObject);
    this.setState({ Categories: temp });
  };

  render() {
    return (
      <div className="App">
        <div className="SplitScreen">
          <ToDoItems data={this.state.Categories} />
          <AddItem onSubmitText={this.onSubmitText} />
        </div>
        <div className="SplitScreen" />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
